import React, {useState} from 'react'
import styles from './Input.module.css'

function Input(props) {
    let [text , setText] = useState('')
    let [amount , setAmount] = useState('')
    let [formIsValid , setFormIsValid] = useState(false)

    let formSubmitHandler = (e) =>{
      e.preventDefault()
      props.onListExpenses(text , amount)
      setText('')
      setAmount('')
    }

    let textChangeHandler = (e) =>{
        setText(e.target.value)
        setFormIsValid(
            e.target.value.trim().length >=1 && amount.trim().length >= 1
        )
        // console.log(text)
    }
    let amountChangeHandler = (e) =>{
        setAmount(e.target.value)
        setFormIsValid(
            e.target.value.trim().length >=1 && text.trim().length >= 1
        )
        // console.log(text)
    }
    return (
        <div className={styles.cover}>
            <h1 className={styles.h1}>Add transaction</h1>
            <form
            onSubmit={formSubmitHandler}>
                <div className={styles.text}>
                    <label>Item purchased</label>
                    <input
                        onChange={textChangeHandler}
                        label = 'Item purchased'
                        type='text'
                        value={text}
                        placeholder = 'Books'
                    />
                </div>
                <div className={styles.text}>
                    <label>Amount</label>
                    <input
                        onChange={amountChangeHandler}
                        label = 'Amount'
                        type='number'
                        value={amount}
                        placeholder = '200'
                    />
                </div>
                <button className={styles.button} disabled={!formIsValid}>Submit</button>
            </form>
        </div>
    )
}

export default Input
